package main

import (
	"blrpc/config"
	"blrpc/controllers"
	"blrpc/repositories"
	"blrpc/services"
	"context"
	"log"
	"net/http"

	"golang.org/x/net/http2"
	"golang.org/x/net/http2/h2c"
	// generated by protoc-gen-go
	// generated by protoc-gen-connect-go
)

func main() {
	// ser := services.NewAppService()
	// gc := controllers.NewGreetController(ser)

	// path, handler := gc.GreetHandler()

	cfg, err := config.New()
	if err != nil {
		log.Fatal(err)
	}
	client := repositories.OpenDB(context.Background(), cfg)
	var ns = services.NewAppService(client)
	var tc = controllers.NewTaskController(ns)

	path, handler := tc.TaskListHandler()

	mux := http.NewServeMux()

	mux.Handle(path, handler)
	http.ListenAndServe(
		"localhost:8080",
		h2c.NewHandler(mux, &http2.Server{}),
	)
}
